<style>
    .courtain {
        height: 100vh;
        width: 100%;
        position: fixed;
        display: flex;
        justify-content: center;
        top: 0;
        transition: height 0.5s ease-in-out, overflow 0s ease 0s;
    }
    
    .logos-wrap {
        width: 60%;
        display: flex;
    }

    .logos-wrap > div {
        width: 100%;
        position: relative;
        display: flex;
        justify-content: center;
        align-items: center;
    }

    .logo {
        max-height: 80%;
        transform-origin: bottom;
        transform: translateY(100%);
        opacity: 0;
        animation: animate-height 2s ease 0s 1 forwards;
    }

    .fuga {
        max-height: 80%;
        transform-origin: top;
        transform: translateY(-100%);
        opacity: 0;
        animation: animate-height 2s ease 0s 1 forwards;
    }

    .font-logo {
        opacity: 0;
        transform: scaleX(0) translateY(50%);
    }

    .animate {
        animation: animate-height 0.3s ease-in-out 0s 1 forwards;
    }

    @keyframes animate-height { 
        to {
            transform: scaleX(1) translateY(0);
            opacity: 1;
        }
    }

    @keyframes animate-height-2{
        to {
            opacity: 1;
        }
    }
</style>